.mapCont{
  @include flx(start , cen);
  width: 100%;
  & + .mapCont{
    margin-top: 0.25rem;
    // @include padding--top(xxs);
    // border-top: dotted thin $rgb__sub;
  }
  &__img{
    background: #ddd;
    height: 250px;
    width: 100%;
    @include margin--top(md);
  }
  &__ttl{
    min-width: 6rem;
    font-weight: 400;
    color: $rgb__main;
  }
  &__txt{
    font-weight: normal;
  }
  &__list{
    @extend .mapCont__txt;
    margin: 1.5rem 0;
  }
}
