result:
.menu{
  position: fixed;
  top: 0;
  left: 0;
  width: 25vw;
  height: 100%;
  background: #f5f5f5;
  padding:2rem 2rem 8rem;
  transition: .3s;
  flex-direction: column;
  @include flx(bet , top);
  flex-direction: column;
  &__nav{
    text-align: left;
    font-size: 14px;
  }
  &__nav__link{
    width: 5rem;
    height: 5rem;
    background: #fff;
    text-align: center;
    line-height: 5rem;
    box-shadow: $shadow__02;
  }
  &__nav__link  + .menu__nav__link{
    margin-top: 1rem;
  }
}

.index .menu{
  width: 35vw;
}
