result:
.brokenGridCont{
  width: 70%;
  margin: auto;
  display: grid;
  grid-template-columns: 30% 1fr;
  grid-template-rows: 1fr auto auto;
  & + .brokenGridCont{
    @include margin--top(lg);
  }
  &__img{
    grid-column: 2/3;
    grid-row: 1/3;
    width: 90%;
    margin-left: auto;
  }
  &__txtArea__hl{
    grid-column: 1/2;
    grid-row: 1/2;
    @include font--size(md);
    @include flx(start , btm);
    color: $rgb__main;
  }
  &__txtArea__ttl{
    grid-column: 1/2;
    grid-row: 2/3;
    font-weight: bold;
    @include margin--top(xxs);
  }
  &__txtArea__txt{
    grid-column: 1/3;
    grid-row: 3/4;
    @include margin--top(sm);
    max-width: 60%;
  }
}
.even.brokenGridCont{
  grid-template-columns: 1fr 40%;
  grid-template-rows: 1fr auto auto;
}
.even .brokenGridCont{
  &__img{
    grid-column: 1/2;
    grid-row: 1/4;
    margin:4rem 3rem 0 0;
    transform: translateY(-4rem);
  }
  &__txtArea__hl{
    grid-column: 2/3;
    grid-row: 1/2;
  }
  &__txtArea__ttl{
    grid-column: 2/3;
    grid-row: 2/3;
  }
  &__txtArea__txt{
    grid-column:2/3;
    grid-row: 3/4;
    @include margin--top(sm);
    max-width: 100%;
  }
}
